import './App.css';
import { useState } from "react";

function App() {
  const [count, setCount] = useState(0);

  const [clickedBoxes, setClickedBoxes] = useState([false, false, false, false]);

  const onClick = (event) => {
      const newClickedBoxes = [...clickedBoxes];
      newClickedBoxes[event] = !newClickedBoxes[event];

      // Set the new state for clicked boxes
      setClickedBoxes(newClickedBoxes);

      // Update the count based on how many boxes are clicked
      const newCount = newClickedBoxes.filter(Boolean).length;  // Count how many `true` values there are
      setCount(newCount);
  };



  return (
      <div className="2x2box">
        <div style={{textAlign: "center"}}>
          <h1>CS5610 Mini Assingment 3</h1>
          <h5>React and JavaScipt</h5>
          <p style={{fontSize: 30, fontWeight: "bold", fontFamily: "gerogia"}}>Count: {count}</p>
        </div>

        <div style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gridTemplateRows: "1fr 1fr",
            width: "200px",
            height: "200px",
            margin: "auto"
        }}>
            <div className="CountBox" style={{ display: "flex", alignItems: "center", justifyContent: "center", border: "1px solid gray" }} onClick={onClick}>1</div>
            <div className="CountBox" style={{ display: "flex", alignItems: "center", justifyContent: "center", border: "1px solid gray" }} onClick={onClick}>2</div>
            <div className="CountBox" style={{ display: "flex", alignItems: "center", justifyContent: "center", border: "1px solid gray" }} onClick={onClick}>3</div>
            <div className="CountBox" style={{ display: "flex", alignItems: "center", justifyContent: "center", border: "1px solid gray" }} onClick={onClick}>4</div>
        </div>
      </div>
  );
}

export default App;
