import './App.css';
import { useState } from "react";

function App() {
    const [count, setCount] = useState(0);

    const handleBoxClick = (event) => {
        const currentColor = event.target.style.backgroundColor;
        event.target.style.backgroundColor = currentColor === "black" ? "" : "black"; // Toggle the background color
    };

    return (
        <div className="2x2box">
            <div style={{ textAlign: "center" }}>
                <h1>CS5610 Mini Assignment 3</h1>
                <h5>React and JavaScript</h5>
                <p style={{ fontSize: 30, fontWeight: "bold", fontFamily: "Georgia" }}>Count: {count}</p>
            </div>

            <div
                style={{
                    display: "grid",
                    gridTemplateColumns: "1fr 1fr",
                    gridTemplateRows: "1fr 1fr",
                    width: "200px",
                    height: "200px",
                    margin: "auto",
                }}
            >
                <div
                    className="CountBox"
                    style={{
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        border: "1px solid gray",
                    }}
                    onClick={handleBoxClick}
                >
                    1
                </div>
                <div
                    className="CountBox"
                    style={{
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        border: "1px solid gray",
                    }}
                    onClick={handleBoxClick}
                >
                    2
                </div>
                <div
                    className="CountBox"
                    style={{
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        border: "1px solid gray",
                    }}
                    onClick={handleBoxClick}
                >
                    3
                </div>
                <div
                    className="CountBox"
                    style={{
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        border: "1px solid gray",
                    }}
                    onClick={handleBoxClick}
                >
                    4
                </div>
            </div>
        </div>
    );
}

export default App;
