import './App.css';
import { useState } from "react";

function App() {
  const [count, setCount] = useState(0);

  const handleBoxClick = (event) => {
      const currentColor = event.target.style.backgroundColor;
      event.target.style.backgroundColor = currentColor === "black" ? "" : "black";
  }

  return (
      <div className="2x2box">
        <div style = {{textAlign: "center"}}>
          <h1>CS5610 Mini Assingment 3</h1>
          <h5>React and JavaScipt</h5>
          <p style = {{fontSize: 30, fontWeight: "bold", fontFamily: "gerogia"}}>Count: {count}</p>
        </div>

        <div style={{
            display: "grid",
            gridTemplateColumns: "1fr 1fr",
            gridTemplateRows: "1fr 1fr",
            width: "200px",
            height: "200px",
            margin: "auto"
        }}>
            <div className="CountBox" style={{ display: "flex", alignItems: "center", justifyContent: "center", border: "1px solid gray" }}>1</div>
            <div className="CountBox" style={{ display: "flex", alignItems: "center", justifyContent: "center", border: "1px solid gray" }}>2</div>
            <div className="CountBox" style={{ display: "flex", alignItems: "center", justifyContent: "center", border: "1px solid gray" }}>3</div>
            <div className="CountBox" style={{ display: "flex", alignItems: "center", justifyContent: "center", border: "1px solid gray" }}>4</div>
        </div>
      </div>
  );
}

export default App;
