import './App.css';
import { CountProvider, useCount } from './CountContext';

function App() {
    return (
        <CountProvider>
            <MainComponent />
        </CountProvider>
    );
}

function MainComponent() {
    const { count, clickedBoxes, onClick } = useCount();

    const boxStyle = {
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        border: "1px solid gray",
    };

    const gridStyle = {
        display: "grid",
        gridTemplateColumns: "1fr 1fr",
        gridTemplateRows: "1fr 1fr",
        width: "200px",
        height: "200px",
        margin: "auto"
    };

    const clickedBox = (index) => ({
        backgroundColor: clickedBoxes[index] ? 'black' : '',
        color: clickedBoxes[index] ? 'white' : ''
    });

    return (
        <div className="2x2box">
            <div style={{textAlign: "center"}}>
                <h1>CS5610 Mini Assignment 3</h1>
                <h5>React and JavaScript</h5>
                <p style={{fontSize: 30, fontWeight: "bold", fontFamily: "Gerogia"}}>Count: {count}</p>
            </div>

            <div style={{...gridStyle}}>
                {clickedBoxes.map((_, index) => (
                    <div
                        key={index}
                        className="CountBox"
                        style={{...boxStyle, ...clickedBox(index)}}
                        onClick={() => onClick(index)}
                    />
                ))}
            </div>
        </div>
    );
}

export default App;
